parameters.jsCode:
// Aggregate all metrics into a comprehensive daily report
const appointmentMetrics = $('Collect Appointment Metrics').all();
const notificationMetrics = $('Collect Notification Metrics').all();
const performanceMetrics = $('Collect System Performance').all();

const report = {
  reportDate: new Date().toISOString().split('T')[0],
  reportType: 'daily_business_metrics',
  generatedAt: new Date().toISOString(),
  metrics: {}
};

// Process appointment metrics
for (const metric of appointmentMetrics) {
  if (metric.json.metric_type === 'daily_summary') {
    report.metrics.appointments = metric.json.metrics;
  } else if (metric.json.metric_type === 'specialty_breakdown') {
    report.metrics.specialties = metric.json.metrics;
  }
}

// Process notification metrics
for (const metric of notificationMetrics) {
  if (metric.json.metric_type === 'notification_summary') {
    report.metrics.notifications = metric.json.metrics;
  }
}

// Process performance metrics
for (const metric of performanceMetrics) {
  if (metric.json.metric_type === 'system_performance') {
    report.metrics.performance = metric.json.metrics;
  }
}

// Calculate derived KPIs
report.kpis = {
  // Operational KPIs
  appointment_utilization: report.metrics.appointments?.appointments?.total || 0,
  patient_satisfaction_proxy: 100 - (report.metrics.appointments?.appointments?.no_show_rate || 0),
  system_reliability: report.metrics.performance?.workflows?.success_rate || 0,

  // Business KPIs
  daily_revenue_proxy: (report.metrics.appointments?.appointments?.completed || 0) * 150, // Assuming avg R$150 per appointment
  queue_efficiency: report.metrics.appointments?.queue?.total_waiting || 0,

  // Communication KPIs
  communication_effectiveness: report.metrics.notifications?.reminder_effectiveness?.effectiveness_rate || 0,
  multi_channel_reach: (report.metrics.notifications?.by_channel || []).length
};

// Add trend analysis (comparison with previous day would require historical data)
report.trends = {
  note: 'Trend analysis requires historical data - implement in next version',
  appointment_growth: 'N/A',
  efficiency_improvement: 'N/A'
};

// Add alerts and recommendations
report.alerts = [];
report.recommendations = [];

// Check for concerning metrics and add alerts
if ((report.metrics.appointments?.appointments?.no_show_rate || 0) > 20) {
  report.alerts.push({
    level: 'warning',
    message: `High no-show rate: ${report.metrics.appointments.appointments.no_show_rate}%`,
    action: 'Review reminder strategy and patient communication'
  });
}

if ((report.metrics.performance?.workflows?.success_rate || 100) < 95) {
  report.alerts.push({
    level: 'error',
    message: `Low workflow success rate: ${report.metrics.performance.workflows.success_rate}%`,
    action: 'Investigate workflow failures immediately'
  });
}

if ((report.metrics.appointments?.queue?.total_waiting || 0) > 50) {
  report.alerts.push({
    level: 'info',
    message: `High queue volume: ${report.metrics.appointments.queue.total_waiting} patients waiting`,
    action: 'Consider adding more appointment slots or doctors'
  });
}

// Add recommendations based on metrics
if ((report.metrics.notifications?.reminder_effectiveness?.effectiveness_rate || 0) < 70) {
  report.recommendations.push({
    category: 'communication',
    suggestion: 'Improve reminder message content and timing',
    impact: 'Reduce no-show rates and improve patient satisfaction'
  });
}

if ((report.metrics.appointments?.appointments?.confirmation_rate || 0) < 80) {
  report.recommendations.push({
    category: 'patient_engagement',
    suggestion: 'Implement more proactive confirmation processes',
    impact: 'Better resource planning and reduced waste'
  });
}

return [{ json: report }];

