parameters.jsCode:
// Check if today is Sunday (end of week) to generate weekly summary
const today = new Date();
const dayOfWeek = today.getDay(); // 0 = Sunday

if (dayOfWeek === 0) {
  // It's Sunday, generate weekly summary
  return [{
    json: {
      shouldGenerateWeekly: true,
      weekEndDate: today.toISOString().split('T')[0],
      weekStartDate: new Date(today.getTime() - (6 * 24 * 60 * 60 * 1000)).toISOString().split('T')[0]
    }
  }];
} else {
  return [{
    json: {
      shouldGenerateWeekly: false,
      message: 'Weekly report only generated on Sundays'
    }
  }];
}
