const services = [
  {
    name: 'WhatsApp API',
    url: 'https://graph.facebook.com/v17.0/me',
    headers: { 'Authorization': `Bearer ${$env.WHATSAPP_ACCESS_TOKEN}` }
  },
  {
    name: 'Google Calendar',
    url: 'https://www.googleapis.com/calendar/v3/users/me/calendarList',
    headers: { 'Authorization': `Bearer ${$env.GOOGLE_ACCESS_TOKEN}` }
  },
  {
    name: 'Twilio SMS',
    url: 'https://api.twilio.com/2010-04-01/Accounts.json',
    auth: {
      username: $env.TWILIO_ACCOUNT_SID,
      password: $env.TWILIO_AUTH_TOKEN
    }
  }
];

const results = [];

for (const service of services) {
  try {
    const response = await $http.request({
      method: 'GET',
      url: service.url,
      headers: service.headers || {},
      auth: service.auth,
      timeout: 10000
    });

    results.push({
      service: service.name,
      status: response.status === 200 ? 'healthy' : 'unhealthy',
      responseTime: Date.now() - startTime,
      statusCode: response.status
    });
  } catch (error) {
    results.push({
      service: service.name,
      status: 'unhealthy',
      error: error.message,
      statusCode: error.response?.status || 0
    });
  }
}

return [{ json: { externalServices: results, checkedAt: new Date().toISOString() } }];

// Aggregate all health check results
const apiHealth = $('Check API Health').first();
const dbHealth = $('Check Database').first();
const redisHealth = $('Check Redis Cache').first();
const externalHealth = $('Check External Services').first();

const healthStatus = {
  timestamp: new Date().toISOString(),
  overall: 'healthy',
  services: {}
};

// Check API health
if (apiHealth?.json?.status === 'ok') {
  healthStatus.services.api = {
    status: 'healthy',
    responseTime: apiHealth.json.responseTime || null,
    version: apiHealth.json.version || null
  };
} else {
  healthStatus.services.api = {
    status: 'unhealthy',
    error: apiHealth?.error || 'API health check failed'
  };
  healthStatus.overall = 'unhealthy';
}

// Check database health
if (dbHealth?.json?.length > 0 && dbHealth.json[0].status === 'healthy') {
  healthStatus.services.database = {
    status: 'healthy',
    appointmentsToday: dbHealth.json[0].appointments_today || 0,
    checkedAt: dbHealth.json[0].checked_at
  };
} else {
  healthStatus.services.database = {
    status: 'unhealthy',
    error: dbHealth?.error || 'Database health check failed'
  };
  healthStatus.overall = 'unhealthy';
}

// Check Redis health
if (redisHealth?.json && !redisHealth.error) {
  healthStatus.services.redis = {
    status: 'healthy',
    info: redisHealth.json
  };
} else {
  healthStatus.services.redis = {
    status: 'unhealthy',
    error: redisHealth?.error || 'Redis health check failed'
  };
  healthStatus.overall = 'degraded'; // Redis failure is not critical
}

// Check external services
if (externalHealth?.json?.externalServices) {
  healthStatus.services.external = {};
  let externalHealthy = 0;
  let externalTotal = externalHealth.json.externalServices.length;

  for (const service of externalHealth.json.externalServices) {
    healthStatus.services.external[service.service.toLowerCase().replace(/\s+/g, '_')] = {
      status: service.status,
      responseTime: service.responseTime,
      error: service.error,
      statusCode: service.statusCode
    };

    if (service.status === 'healthy') {
      externalHealthy++;
    }
  }

  // If more than half of external services are down, mark as degraded
  if (externalHealthy < externalTotal / 2) {
    if (healthStatus.overall === 'healthy') {
      healthStatus.overall = 'degraded';
    }
  }
} else {
  healthStatus.services.external = {
    status: 'unknown',
    error: 'External services check failed'
  };
}

// Calculate uptime and performance metrics
healthStatus.metrics = {
  servicesHealthy: Object.values(healthStatus.services).filter(s => s.status === 'healthy' || (typeof s === 'object' && Object.values(s).some(ss => ss.status === 'healthy'))).length,
  servicesTotal: Object.keys(healthStatus.services).length,
  lastCheck: healthStatus.timestamp
};

return [{ json: healthStatus }];

