parameters.jsCode:
// Check N8N workflow health
const workflowsData = $('Check N8N Workflows').first();

if (!workflowsData || workflowsData.error) {
  return [{
    json: {
      workflowHealth: {
        status: 'unhealthy',
        error: workflowsData?.error || 'Failed to fetch workflows',
        activeWorkflows: 0,
        totalWorkflows: 0
      }
    }
  }];
}

const workflows = workflowsData.json.data || [];
const activeWorkflows = workflows.filter(w => w.active).length;
const totalWorkflows = workflows.length;

// Core workflows that must be active
const coreWorkflows = [
  'novo-agendamento',
  'sistema-lembretes',
  'gestao-fila-espera',
  'reagendamento',
  'monitoring-sistema'
];

const activeCoreWorkflows = workflows.filter(w => 
  w.active && coreWorkflows.includes(w.name)
).length;

let status = 'healthy';
let issues = [];

if (activeCoreWorkflows < coreWorkflows.length) {
  status = 'degraded';
  issues.push(`${coreWorkflows.length - activeCoreWorkflows} core workflows inactive`);
}

if (activeWorkflows === 0) {
  status = 'unhealthy';
  issues.push('No workflows are active');
}

return [{
  json: {
    workflowHealth: {
      status,
      activeWorkflows,
      totalWorkflows,
      activeCoreWorkflows,
      totalCoreWorkflows: coreWorkflows.length,
      issues: issues.length > 0 ? issues : null,
      checkedAt: new Date().toISOString()
    }
  }
}];
